(function($){$.fn.jslide=function(){var defaults={time:5000};return this.each(function(){var $this=$(this);var sWidth=$(this).find(".s-slide-pic").width();var len=$this.find("ul li").length;var index=0;var picTimer;var btn="<div class='s-but'><div class='arrow prev'></div><div class='arrow next'></div></div>";$this.append(btn);$this.find(".prev").click(function(){index-=1;if(index==-1){index=len-1}
    showPics(index);});$this.find(".next").click(function(){index+=1;if(index==len){index=0}
    showPics(index);});$this.find("ul").css("width",sWidth*(len));function showPics(index){var nowLeft=-index*sWidth;$this.find("ul").stop(true,false).animate({"left":nowLeft},300);}})}})(jQuery);