var doc=window.document,input=doc.createElement("input");if(typeof input["placeholder"]=="undefined"){$("input").each(function(ele){var me=$(this);var ph=me.attr("placeholder");if(ph&&!me.val()){me.val(ph).css("color","#aaa")}me.on("focus",function(){if(me.val()===ph){me.val(null)}}).on("blur",function(){if(!me.val()){me.val(ph).css("color","#aaa")}})})};